--------------------------LIVE JOB-----------------Declare @Deadline As varchar(10) = CONVERT(varchar(10), GETDATE(), 101)		,@PublishDate As varchar(10) = CONVERT(varchar(10), GETDATE()+1, 101)SELECT COUNT(JP_ID) AS LiveJobFROM DBO_JOBPOSTINGS JWHERE J.Verified = 1 And J.Drafted = 0 And J.Closed = 0 And J.Deadline >= @Deadline And J.PublishDate < @PublishDateAND RegionalJob <> 4------------------------------------PAUSED JOB-------------------------Declare @Deadline As varchar(10) = CONVERT(varchar(10), GETDATE(), 101)		,@PublishDate As varchar(10) = CONVERT(varchar(10), GETDATE()+1, 101)SELECT COUNT(JP_ID) AS PausedJobFROM DBO_JOBPOSTINGS JWHERE J.Verified = 1 And J.Drafted = 0 And J.Closed = 1 And J.PublishDate >= '05/01/2023'AND RegionalJob <> 4------------------------------------Expired JOB-------------------------Declare @Deadline As varchar(10) = CONVERT(varchar(10), GETDATE(), 101)		,@PublishDate As varchar(10) = CONVERT(varchar(10), GETDATE()+1, 101)SELECT COUNT(JP_ID) AS ExpiredJobFROM DBO_JOBPOSTINGS JWHERE J.Verified = 1 And J.Drafted = 0 And J.Closed = 0 And J.PublishDate >= '05/01/2023' And J.Deadline < @DeadlineAND RegionalJob <> 4------------------------------------Pending JOB-------------------------Declare @Deadline As varchar(10) = CONVERT(varchar(10), GETDATE(), 101)		,@PublishDate As varchar(10) = CONVERT(varchar(10), GETDATE()+1, 101)SELECT COUNT(JP_ID) AS PendingJobFROM DBO_JOBPOSTINGS JWHERE J.PostingDate >= '05/01/2023' And J.PublishDate IS NULLAND RegionalJob <> 4------------------------------------Pending JOB-------------------------Declare @Deadline As varchar(10) = CONVERT(varchar(10), GETDATE(), 101)		,@PublishDate As varchar(10) = CONVERT(varchar(10), GETDATE()+1, 101)SELECT COUNT(JP_ID) AS PendingJobFROM DBO_JOBPOSTINGS JWHERE J.PostingDate >= '05/01/2023' --And J.PublishDate IS NULLAND J.DeadLine > @Deadline AND J.VERIFIED = 0 AND J.Drafted = 0SELECT COUNT(CP_ID) FROM DBO_JOB_INBOX INNER JOI