-- Total Applicant sent invitation





with maincte as (Select distinct ji.ApplyID,name, e_mail1from DBO_JOB_INBOX jiinner join bdjresumes.dbo.personal p on ji.p_id = p.id --and name   not like '%test%' and name   not like 'dilruba' and name not like '%mridha%' and name not like '%Jack Sparrow%' and e_mail1 not like '%rumana%' and name not like '%New Update threePM Thirty First threePM%'INNER JOIN rp.TestSteps rp ON ji.Jp_ID = rp.JP_ID AND rp.TestType = 'aiasmnt'--inner join aiass.AIAssessmentinfo a on ji.jp_id = a.JP_IDINNER JOIN aiass.AIAssessmentapplicants aa ON rp.stId = aa.StId and aa.ApplyId = ji.ApplyIDINNER JOIN rp.ApplicantProcess ap on rp.TestLevel = ap.LevelStatus and ji.ApplyID = ap.ApplyIdWHere rp.CreatedOn >= '2024-03-10 14:00:00' and rp.CreatedOn < GETDATE()       --and a.PostedOn >= '2024-03-10 14:00:00' and a.PostedOn < GETDATE()     and aa.InvitedOn is not null and aa.InvitedOn <> '' and ap.Notify = 1	 )	 select count( *) 'No. of applicants invited for AI Assessment'	 from maincte;-- Assessment Submittedwith maincte as (Select distinct ji.ApplyID,FinalSubmitted, DeviceTypefrom DBO_JOB_INBOX jiinner join bdjresumes.dbo.personal p on ji.p_id = p.id and name   not like '%test%' and name   not like 'dilruba' and name not like '%mridha%' and name not like '%Jack Sparrow%' and e_mail1 not like '%rumana%' and name not like '%New Update threePM Thirty First threePM%'INNER JOIN rp.TestSteps rp ON ji.Jp_ID = rp.JP_ID AND rp.TestType = 'aiasmnt'--inner join aiass.AIAssessmentinfo a on ji.jp_id = a.JP_IDinner JOIN aiass.AIAssessmentapplicants aa ON rp.stId = aa.StId and aa.ApplyId = ji.ApplyID--inner join rp.ApplicantProcess ap on rp.TestLevel = ap.LevelStatus and ji.ApplyID = ap.ApplyIdWHere rp.CreatedOn >= '2024-03-10 14:00:00' and rp.CreatedOn < GETDATE()       --and a.PostedOn >= '2024-03-10 14:00:00'      and aa.InvitedOn is not null and aa.InvitedOn <> '' --and ap.Notify = 1 	 )	 select count( *) 'No. of applicants submitted AI Assessment (Web)'	 from maincte	 where FinalSubmitted = 1--assessment created by companywith maincte as (Select  jp.Cp_IDfrom dbo_jobpostings jpINNER JOIN rp.TestSteps rp ON jp.Jp_ID = rp.JP_ID AND rp.TestType = 'aiasmnt'LEFT JOIN aiass.AIAssessmentInfo aa ON rp.jp_id = aa.jp_idWHere rp.CreatedOn >= '2024-03-10 14:00:00' and rp.CreatedOn < '2024-04-21' and VERIFIED = 1     --- and [PostedOn] >= '2024-03-10 14:00:00' and[PostedOn] < GETDATE()	 )	 select count( *) 'No. of unique companies created AI Assessment'	 from maincte;