WITH CV_CTE AS (	SELECT  p.ID, --MIN(p.ID), MAX(p.ID)		Ph = CASE WHEN p.PhotoUploaded = 1 THEN 20 ELSE 0 END,		Ed = CASE WHEN (SELECT TOP 1 Ed_ID FROM bdjResumes..EDU WHERE p.ID = P_ID) > 0 THEN 20 ELSE 0 END,		Tr = CASE WHEN (SELECT TOP 1 T_ID FROM bdjResumes..TRAINING WHERE p.ID = P_ID) > 0 THEN 5 ELSE 0 END,		Pr = CASE WHEN (SELECT TOP 1 Pe_ID FROM bdjResumes..PEDU WHERE p.ID = P_ID) > 0 THEN 5 ELSE 0 END,		Ex = CASE WHEN (SELECT TOP 1 Ex_ID FROM bdjResumes..EXP WHERE p.ID = P_ID) > 0 THEN 20 ELSE 0 END,		Sp = CASE WHEN (SELECT TOP 1 P_ID FROM bdjResumes..SPECIALIST WHERE p.ID = P_ID) > 0 THEN 5 ELSE 0 END,		Rf = CASE WHEN (SELECT TOP 1 R_ID FROM bdjResumes..REF WHERE p.ID = P_ID) > 0 THEN 5 ELSE 0 END,		CV = CASE WHEN u.CVPosted = 1 THEN 20 ELSE 0 END 	FROM bdjResumes..PERSONAL AS p		LEFT JOIN bdjResumes..UserAccounts AS u ON p.ID = u.accID		--WHERE p.ID <= 7086042		--WHERE p.ID >= 5101 AND p.ID <= 7083030		), completeCTE AS (	SELECT ID,		(Ph + Ed + Tr + Pr + Ex + Sp + Rf + CV) AS [CV_Complete]	FROM CV_CTE)SELECT 	COUNT(DISTINCT CASE WHEN e.[CV_Complete] = 20 THEN e.ID END) AS [20 percent Complete] FROM completeCTE e--LEFT JOIN bdjCorporate..DBO_JOB_INBOX AS j ON e.ID = j.P_ID--5157822  -- Total ID -- 517421 -- 20% complete